const caseStudies=[{id:1,company:"Nox Advaita",title:"The Journey Of Nox Advaita : Designing The Future Of Medical Devices.",image:"../assets/CASE-STUDY-BANNER/NOX ADVAITA.webp",url:"https://imacengineering.com/the-journey-of-nox-advaita"},{id:2,company:"Gynec Bed",title:"Gynec Bed By Karma : Revolutionizing Patient Care",image:"../assets/CASE-STUDY-BANNER/GYNEC BED.webp",url:"https://imacengineering.com/gynec-bed-by-karma"},{id:3,company:"EV Scooter",title:"EV Scooter Development",image:"../assets/CASE-STUDY-BANNER/EV VEHICLE.webp",url:"https://imacengineering.com/ev-scooter-development"},{id:4,company:"IR Blaster",title:"Development of a Universal IR Blaster for Smart Device Control",image:"../assets/CASE-STUDY-BANNER/IR BLASTER.webp",url:"https://imacengineering.com/universal-IR-blaster"},{id:5,company:"Headphones",title:"Gaming Headphones",image:"../assets/CASE-STUDY-BANNER/GAMING HEADPHONE.webp",url:"https://imacengineering.com/gaming-headphones"},{id:6,company:"Pill Cap",title:"Pill Cap",image:"../assets/CASE-STUDY-BANNER/PILLCAP.webp",url:"https://imacengineering.com/pill-cap"},{id:7,company:"Centrifuge Machine",title:"Centrifuge Machine",image:"../assets/CASE-STUDY-BANNER/CENTRIFUGE NACHINE.webp",url:"https://imacengineering.com/centrifuge-machine"},{id:8,company:"Caravan Fan",title:"Portable Caravan Fan",image:"../assets/CASE-STUDY-BANNER/PORTABLE CARAVAN FAN.webp",url:"https://imacengineering.com/portable-caravan-fan"},{id:9,company:"AFDS",title:"AFDS: Advanced Fire Detection System",image:"../assets/CASE-STUDY-BANNER/AFDS.webp",url:"https://imacengineering.com/advanced-fire-detection-system"},{id:10,company:"Patient Warmer",title:"Patient Warmer",image:"../assets/CASE-STUDY-BANNER/PATIENT WARMER.webp",url:"https://imacengineering.com/patient-warmer"},{id:11,company:"Cataract Foot Pedal",title:"Cataract Foot Pedal",image:"../assets/CASE-STUDY-BANNER/Cataract foot padel.webp",url:"https://imacengineering.com/cataract-foot-pedal"}];class CaseStudiesApp{constructor(){this.gridElement=document.getElementById('caseStudiesGrid');this.init()}
init(){this.setupEventListeners();if(this.gridElement){this.renderCaseStudies()}else{console.error('Element #caseStudiesGrid not found');}}
setupEventListeners(){document.addEventListener('click',(e)=>{if(e.target.matches('a[href^="#"]')){e.preventDefault();const target=document.querySelector(e.target.getAttribute('href'));if(target){target.scrollIntoView({behavior:'smooth'})}}});this.setupImageLazyLoading()}
setupImageLazyLoading(){if('IntersectionObserver' in window){const imageObserver=new IntersectionObserver((entries,observer)=>{entries.forEach(entry=>{if(entry.isIntersecting){const img=entry.target;img.src=img.dataset.src;img.classList.remove('lazy');imageObserver.unobserve(img)}})});setTimeout(()=>{document.querySelectorAll('img[data-src]').forEach(img=>{imageObserver.observe(img)})},100)}}
getAllCaseStudies(){return caseStudies}
createCaseStudyCard(study){const card=document.createElement('a');card.className='case-study-card fade-in';card.href=study.url;card.rel='noopener noreferrer';card.innerHTML=`
            <img 
                class="case-study-image lazy" 
                data-src="${study.image}" 
                alt="${study.company} - ${study.title}"
                onerror="this.style.display='none'"
            >
            <div class="case-study-content">
                <div class="case-study-company">${study.company}</div>
                <h3 class="case-study-title">${study.title}</h3>
            </div>
        `;return card}
renderCaseStudies(){const allStudies=this.getAllCaseStudies();if(!this.gridElement){console.error('Element #caseStudiesGrid not found');return;}this.gridElement.innerHTML='';if(allStudies.length===0){this.renderEmptyState();return}
const fragment=document.createDocumentFragment();allStudies.forEach(study=>{const card=this.createCaseStudyCard(study);fragment.appendChild(card)});this.gridElement.appendChild(fragment);this.setupImageLazyLoading();setTimeout(()=>{const cards=this.gridElement.querySelectorAll('.case-study-card');cards.forEach((card,index)=>{setTimeout(()=>{card.style.opacity='1';card.style.transform='translateY(0)'},index*50)})},50)}
renderEmptyState(){if(!this.gridElement){console.error('Element #caseStudiesGrid not found');return;}this.gridElement.innerHTML=`
            <div class="empty-state">
                <h3>No case studies found</h3>
                <p>Try selecting a different category to see more work.</p>
            </div>
        `}}
document.addEventListener('DOMContentLoaded',()=>{if(document.getElementById('caseStudiesGrid')){new CaseStudiesApp()}});document.addEventListener('error',(e)=>{if(e.target.tagName==='IMG'){e.target.style.display='none';console.warn('Failed to load image:',e.target.src)}},!0);function preloadCriticalImages(){const criticalImages=caseStudies.slice(0,6).map(study=>study.image);criticalImages.forEach(src=>{const link=document.createElement('link');link.rel='preload';link.as='image';link.href=src;document.head.appendChild(link)})}
window.addEventListener('load',preloadCriticalImages);class HorizontalCarousel{constructor(){this.track=document.getElementById('carouselTrack');this.prevBtn=document.getElementById('prevBtn');this.nextBtn=document.getElementById('nextBtn');this.indicators=document.getElementById('carouselIndicators');this.originalCards=document.querySelectorAll('.carousel-card');this.currentIndex=0;this.cardWidth=350;this.gap=20;this.cardsPerView=this.calculateCardsPerView();this.totalOriginalCards=this.originalCards.length;this.isAnimating=!1;this.autoScrollInterval=null;this.touchStartX=0;this.touchEndX=0;this.infiniteLoopEnabled=!0;if(this.track&&this.prevBtn&&this.nextBtn&&this.indicators){this.init()}else{console.error('Carousel: Required elements not found. Check for #carouselTrack, #prevBtn, #nextBtn, #carouselIndicators')}}
init(){this.updateCardDimensions();this.setupInfiniteLoop();this.createIndicators();this.bindEvents();this.updateCarousel();this.startAutoScroll();window.addEventListener('resize',()=>{this.updateCardDimensions();this.setupInfiniteLoop();this.updateCarousel()})}
updateCardDimensions(){if(!this.track||!this.track.parentElement){console.error('Carousel track element not found');return;}const containerWidth=this.track.parentElement.offsetWidth;const screenWidth=window.innerWidth;if(screenWidth<=480){this.cardWidth=250}else if(screenWidth<=768){this.cardWidth=280}else if(screenWidth<=1200){this.cardWidth=300}else{this.cardWidth=350}
this.cardsPerView=this.calculateCardsPerView()}
calculateCardsPerView(){if(!this.track||!this.track.parentElement){return 1;}const containerWidth=this.track.parentElement.offsetWidth;return Math.floor(containerWidth/(this.cardWidth+this.gap))}
setupInfiniteLoop(){if(!this.infiniteLoopEnabled)return;const clonedCards=this.track.querySelectorAll('[data-cloned]');clonedCards.forEach(card=>card.remove());const cardCount=this.cardsPerView*2;for(let i=0;i<cardCount;i++){const originalIndex=i%this.totalOriginalCards;const originalCard=this.originalCards[originalIndex];const clonedCard=originalCard.cloneNode(!0);clonedCard.setAttribute('data-cloned','end');this.track.appendChild(clonedCard)}
for(let i=0;i<cardCount;i++){const originalIndex=(this.totalOriginalCards-1-i%this.totalOriginalCards+this.totalOriginalCards)%this.totalOriginalCards;const originalCard=this.originalCards[originalIndex];const clonedCard=originalCard.cloneNode(!0);clonedCard.setAttribute('data-cloned','start');this.track.insertBefore(clonedCard,this.track.firstChild)}
this.cards=this.track.querySelectorAll('.carousel-card');this.currentIndex=cardCount;this.updateCarousel(!1)}
createIndicators(){if(!this.indicators){console.error('Element #carouselIndicators not found');return;}this.indicators.innerHTML='';const indicatorCount=this.totalOriginalCards;for(let i=0;i<indicatorCount;i++){const indicator=document.createElement('div');indicator.classList.add('indicator');if(i===0)indicator.classList.add('active');indicator.addEventListener('click',()=>{this.goToSlide(i)});this.indicators.appendChild(indicator)}}
bindEvents(){this.prevBtn.addEventListener('click',()=>this.prevSlide());this.nextBtn.addEventListener('click',()=>this.nextSlide());this.track.addEventListener('touchstart',(e)=>this.handleTouchStart(e));this.track.addEventListener('touchend',(e)=>this.handleTouchEnd(e));this.track.addEventListener('mousedown',(e)=>this.handleMouseDown(e));this.track.addEventListener('mouseenter',()=>this.stopAutoScroll());this.track.addEventListener('mouseleave',()=>this.startAutoScroll());document.addEventListener('keydown',(e)=>{if(e.key==='ArrowLeft')this.prevSlide();if(e.key==='ArrowRight')this.nextSlide();})}
handleTouchStart(e){this.touchStartX=e.touches[0].clientX;this.stopAutoScroll()}
handleTouchEnd(e){this.touchEndX=e.changedTouches[0].clientX;this.handleSwipe();this.startAutoScroll()}
handleMouseDown(e){e.preventDefault();this.touchStartX=e.clientX;this.stopAutoScroll();const handleMouseMove=(moveEvent)=>{this.touchEndX=moveEvent.clientX};const handleMouseUp=()=>{this.handleSwipe();this.startAutoScroll();document.removeEventListener('mousemove',handleMouseMove);document.removeEventListener('mouseup',handleMouseUp)};document.addEventListener('mousemove',handleMouseMove);document.addEventListener('mouseup',handleMouseUp)}
handleSwipe(){const swipeThreshold=50;const swipeDistance=this.touchStartX-this.touchEndX;if(Math.abs(swipeDistance)>swipeThreshold){if(swipeDistance>0){this.nextSlide()}else{this.prevSlide()}}}
prevSlide(){if(this.isAnimating)return;if(this.infiniteLoopEnabled){this.currentIndex--;this.updateCarousel();this.checkInfiniteLoop()}else{if(this.currentIndex>0){this.currentIndex--;this.updateCarousel()}}}
nextSlide(){if(this.isAnimating)return;if(this.infiniteLoopEnabled){this.currentIndex++;this.updateCarousel();this.checkInfiniteLoop()}else{if(this.currentIndex<this.totalOriginalCards-this.cardsPerView){this.currentIndex++;this.updateCarousel()}}}
goToSlide(index){if(this.isAnimating)return;if(this.infiniteLoopEnabled){const clonedCardCount=this.cardsPerView*2;this.currentIndex=clonedCardCount+index;this.updateCarousel()}else{this.currentIndex=Math.max(0,Math.min(this.totalOriginalCards-this.cardsPerView,index));this.updateCarousel()}}
updateCarousel(animate=!0){if(this.isAnimating&&animate)return;if(animate){this.isAnimating=!0}
const translateX=-(this.currentIndex*(this.cardWidth+this.gap));if(animate){this.track.style.transition='transform 0.6s cubic-bezier(0.25, 0.46, 0.45, 0.94)'}else{this.track.style.transition='none'}
this.track.style.transform=`translateX(${translateX}px)`;this.updateNavigationButtons();this.updateIndicators();if(animate){setTimeout(()=>{this.isAnimating=!1},600)}}
checkInfiniteLoop(){if(!this.infiniteLoopEnabled)return;const clonedCardCount=this.cardsPerView*2;const totalCards=this.cards.length;setTimeout(()=>{if(this.currentIndex>=totalCards-clonedCardCount){this.currentIndex=clonedCardCount;this.updateCarousel(!1)}else if(this.currentIndex<clonedCardCount){this.currentIndex=totalCards-clonedCardCount-1;this.updateCarousel(!1)}},600)}
updateNavigationButtons(){if(this.infiniteLoopEnabled){this.prevBtn.classList.remove('disabled');this.nextBtn.classList.remove('disabled')}else{this.prevBtn.classList.toggle('disabled',this.currentIndex<=0);this.nextBtn.classList.toggle('disabled',this.currentIndex>=this.totalOriginalCards-this.cardsPerView)}}
updateIndicators(){const indicators=this.indicators.querySelectorAll('.indicator');let activeIndicatorIndex;if(this.infiniteLoopEnabled){const clonedCardCount=this.cardsPerView*2;const adjustedIndex=this.currentIndex-clonedCardCount;activeIndicatorIndex=((adjustedIndex%this.totalOriginalCards)+this.totalOriginalCards)%this.totalOriginalCards}else{activeIndicatorIndex=this.currentIndex}
indicators.forEach((indicator,index)=>{indicator.classList.toggle('active',index===activeIndicatorIndex)})}
startAutoScroll(){this.stopAutoScroll();this.autoScrollInterval=setInterval(()=>{this.nextSlide()},3000)}
stopAutoScroll(){if(this.autoScrollInterval){clearInterval(this.autoScrollInterval);this.autoScrollInterval=null}}
destroy(){this.stopAutoScroll();window.removeEventListener('resize',this.updateCardDimensions)}}
document.addEventListener('DOMContentLoaded',()=>{const carousel=new HorizontalCarousel();const images=document.querySelectorAll('.carousel-card img');images.forEach(img=>{img.addEventListener('load',()=>{img.style.opacity='1'});img.addEventListener('error',()=>{img.style.background='#333';img.style.display='flex';img.style.alignItems='center';img.style.justifyContent='center';img.innerHTML='<i class="fas fa-image" style="font-size: 3rem; color: #666;"></i>'})});const observerOptions={root:null,rootMargin:'50px',threshold:0.1};const imageObserver=new IntersectionObserver((entries)=>{entries.forEach(entry=>{if(entry.isIntersecting){const img=entry.target;if(img.dataset.src){img.src=img.dataset.src;img.removeAttribute('data-src')}
imageObserver.unobserve(img)}})},observerOptions);images.forEach(img=>{if(img.dataset.src){imageObserver.observe(img)}})});if(typeof module!=='undefined'&&module.exports){module.exports=HorizontalCarousel}